import Form from 'react-bootstrap/Form';
import { Row, Col, Grid } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';

// const UserInfo = [
//     {link: "/", name: "Program"},
//     {link: "/info", name: "Info"},
//     {link: "/booking", name: "Booking"},
//     {link: "/FAQ", name: "FAQ"},
//     {link: "/contact", name: "Contact"},
//   ]

//   const UILink = ({link, name}) => 
//     <LI_list>
//       <Alink to={link} onClick={this.toggleExpandedMenu}> {name} </Alink> <Span />
//     </LI_list>

//   const UILinkContainer = () => routes.map(UILink)

class UserForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            first_name: '',
            last_name: '',
            email_address: '',
            phone_number: '',
            company_name: '',
            status: 'pending',
            primary_gl: false,
            primary_el: false
        }

        this.onChange = this.onChange.bind(this);
        // this.onSubmit = this.onSubmit.bind(this);
        // this.checkUserExists = this.checkUserExists.bind(this);
    }

    onChange(e) {
        this.setState({ [e.target.name]: e.target.value });
        console.log(this.state, e.target.name, 'this state from login')
    }

    onSubmit(e) {
        e.preventDefault();
        console.log("its hitting onSubmit")
    }

    render() {
        return (
            <div>
                <Form onSubmit={this.onSubmit}>

                    <Form.Row>

                        <Form.Group as={Col} controlId="formGridName">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control type="name" placeholder="First Name"  name="first_name" onChange={this.onChange}/>
                        </Form.Group>

                        <Form.Group as={Col} controlId="formGridLastName">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control type="lastName" placeholder="Last name" name="last_name" onChange={this.onChange} />
                        </Form.Group>
                    </Form.Row>


                    <Form.Row>

                        <Form.Group as={Col} controlId="formGridEmail">
                            <Form.Label>Email</Form.Label>
                            <Form.Control type="email" placeholder="Enter email" name="email_address" onChange={this.onChange}/>
                        </Form.Group>

                        <Form.Group as={Col} controlId="formGridPhoneNumber">
                            <Form.Label>Phone Number</Form.Label>
                            <Form.Control type="phone_number" placeholder="phone number" name="phone_number" onChange={this.onChange}/>
                        </Form.Group>
                    </Form.Row>

                    <Form.Group controlId="formGridAddress1">
                        <Form.Label>Address</Form.Label>
                        <Form.Control placeholder="1234 Main St" />
                    </Form.Group>

                    <Form.Group controlId="formGridCompanyName">
                        <Form.Label>Address 2</Form.Label>
                        <Form.Control placeholder="Apartment, studio, or floor" />
                    </Form.Group>
{/* 
                    <Form.Row>
                        <Form.Group as={Col} controlId="formGridCity">
                            <Form.Label>City</Form.Label>
                            <Form.Control />
                        </Form.Group>

                        <Form.Group as={Col} controlId="formGridState">
                            <Form.Label>State</Form.Label>
                            <Form.Control as="select" value="Choose...">
                                <option>Choose...</option>
                                <option>...</option>
                            </Form.Control>
                        </Form.Group>

                        <Form.Group as={Col} controlId="formGridZip">
                            <Form.Label>Zip</Form.Label>
                            <Form.Control />
                        </Form.Group>
                    </Form.Row> */}

                    <Form.Group id="formGridCheckbox">
                        <Form.Check type="checkbox" label="Check me out" />
                    </Form.Group>
                    <Form.Group id="formGridCheckbox">
                        <Form.Check type="checkbox" label="Check me out" />
                    </Form.Group>
                    <Form.Group id="formGridCheckbox">
                        <Form.Check type="checkbox" label="Check me out" />
                    </Form.Group>


                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>
            </div>
        )
    }
}

export default UserForm;
