import Form from 'react-bootstrap/Form';
import { Row, Col, Grid } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';

// const UserInfo = [
//     {link: "/", name: "Program"},
//     {link: "/info", name: "Info"},
//     {link: "/booking", name: "Booking"},
//     {link: "/FAQ", name: "FAQ"},
